import React, { useState, useContext, useEffect } from "react"
import { View, TextInput, Text } from "react-native"
import { Title, Subheading, Button, Paragraph } from "react-native-paper"
import { Icon } from "../../components/SVG/Icon/Icon"
import { AuthContext } from "../../providers/AuthProvider/AuthProvider"
import * as Location from 'expo-location'
import { createPost } from "../../api/service"
import HappyHourFilled from '../../../assets/icons/happyHourFilled.svg'
import WhatsHappeningFilled from '../../../assets/icons/whatsHappeningFilled.svg'
import DealsFilled from '../../../assets/icons/dealsFilled.svg'
import MiscFilled from '../../../assets/icons/miscFilled.svg'
import RecFilled from '../../../assets/icons/recFilled.svg'

export const CreatePost = () => {
    const [ title, setTitle ] = useState('')
    const [ content, setContent ] = useState('')
    const [category, setCategory] = useState('')
    const  {credentials} = useContext(AuthContext)

    const [location, setLocation] = useState<{
        longitude: undefined;
        latitude: undefined;
        } | {
        longitude: number;
        latitude: number;  
        }>({
        longitude: undefined,
        latitude: undefined,
    })

    const {longitude, latitude} = location
    useEffect(() => {
        (async () => {
            let location = await Location.getCurrentPositionAsync({});
            //@ts-ignore
            const { coords } = location
            const longitude = coords.longitude
            const latitude = coords.latitude
            //@ts-ignore
            setLocation({ longitude: longitude, latitude: latitude })
        })();
    }, [])

    const createPostAction = () => {
        if(longitude && latitude){
            createPost({content, title, token: credentials!, longitude, latitude, categoryId: 1 })
        }
    }

    const rec = "Recreation"
    const happyHour = "Happy_Hour"
    const misc = "Misc" 
    const deals = "Deals"
    const whatsHappening = "What's_Happening?"

    return (
        <View >
            <Text style={{textAlign: 'center', marginVertical: 45, color:'white', fontWeight:'500', fontSize: 34}}> Create Your Post</Text>
                <Subheading style={{ marginHorizontal:24, fontSize: 20}}> Category </Subheading>
                <View style={{ flexDirection: 'row', justifyContent: 'space-between', marginHorizontal: '8%',marginTop:'5%', alignContent:'center', alignItems:'center', marginBottom: '5%'}}>
                {
                    category === deals 
                        ? <DealsFilled width={45} height={45}/>
                        : <Icon name="deals"  size={40} color={'white'} onPress={() => setCategory(deals)}/>
                }
                {
                    category === happyHour 
                        ? <HappyHourFilled width={45} height={45}/>
                        : <Icon name="happyHour"  size={40} color={'white'} onPress={() => setCategory(happyHour)}/>
                }
                {
                    category === rec 
                        ? <RecFilled width={45} height={45}/>
                        : <Icon name="rec"  size={40} color={'white'} onPress={() => setCategory(rec)}/>
                }
                { 
                    category === whatsHappening 
                        ? <WhatsHappeningFilled width={45} height={45}/>
                        : <Icon name="whatsHappening"  size={40} color={'white'} onPress={() => setCategory(whatsHappening)}/>
                }
                {
                    category === misc 
                        ? <MiscFilled width={45} height={45}/> 
                        : <Icon name="misc"  size={40} color={'white'} onPress={() => setCategory(misc)}/>
                }
            </View>
            <View style={{marginHorizontal: 24, marginBottom: '5%'}}>
            <Subheading style={{marginBottom: '5%', fontSize: 20}}> Where </Subheading> 
            <TextInput
                placeholder='Location of post!'
                value={title}
                onChangeText={title => setTitle(title)}
                textAlign={'left'}
                style={{
                    padding:20,
                    backgroundColor:'white', 
                    borderRadius: 15,
                    width: '100%', 
                    alignSelf:'center'
                }}
            />
            </View>
            <View style={{marginHorizontal: 24}}>
            <Subheading style={{fontSize: 20, marginBottom: '5%'}}> Content </Subheading>
            <TextInput
                placeholder='Write some content for your post!'
                value={content}
                onChangeText={content => setContent(content)}
                textAlign={'left'}
                style={{
                    minHeight:150,
                    backgroundColor:'white',
                    borderRadius: 20,
                    width: '100%', 
                    alignSelf:'center',
                    padding:20,
                    paddingTop:20
                }}
                multiline={true}
            />
            </View>
            <Button mode='contained' style={{width: 200, alignSelf:'center', marginVertical: 12, backgroundColor:'rgb(30, 147,180)'}} onPress={() => createPostAction()}>
                <Text style={{fontSize: 20, fontWeight: '600', color:'white'}}>
                Submit
                </Text>
            </Button>
        </View>
    )
}
