import React, { useContext, useEffect } from 'react'
import { useState } from 'react'
import { View, Text, TextInput } from 'react-native'
import { Button, Title } from 'react-native-paper'
import { submitContentReview } from '../../api/service'
import { ScreenContainer } from '../../components/ScreenContainer/ScreenContainer'
import { AuthContext } from '../../providers/AuthProvider/AuthProvider'
import * as Location from 'expo-location';
import { DARKBLUE, LIGHTBLUE, LIGHTERBLUE } from '../../utils/constants'
import Cog from '../../../assets/icons/feedbackCog.svg'

export const Report = ({route}) => {

  const {credentials} = useContext(AuthContext)
  const [feedback, setFeedback] = useState('')
  const {data: routeData} = route.params

  const { id, type } = routeData

  const postFeedback = () => {
    if(feedback.length !== 0){
      submitContentReview(credentials!, {
        postId: type === 'post' ? id : undefined,
        commentId: type === 'comment' ? id : undefined,
        content: feedback,
        contentType: type
      })
    }
  }

  return (
    <ScreenContainer>
      <View style={{alignItems:'center', }}>
        <View style={{flexDirection:'row', marginVertical:12, alignItems:'center'}}>
          <Cog width={40} height={50}/>
          <Text style={{textAlign:'center', fontSize: 36, color: 'white', fontWeight:'600', marginHorizontal: 20}}>Report</Text>
          <Cog width={40} height={50}/>
        </View>
        <TextInput
            placeholder='Enter report here...'
            value={feedback}
            onChangeText={(text) => setFeedback(text)}
            textAlign={'left'}
            style={{
                marginTop: 20,
                minHeight:100,
                backgroundColor: LIGHTERBLUE,
                borderColor:DARKBLUE,
                borderRadius: 20,
                borderWidth: 2,
                width: '85%', 
                alignSelf:'center',
                padding:20,
                paddingTop:20
            }}
            multiline={true}
        />
        <Button style={{ marginVertical: 12, paddingHorizontal: 10, backgroundColor: LIGHTERBLUE, borderWidth: 2, borderColor: DARKBLUE }} onPress={() => postFeedback()} mode='contained'>
          <Title style={{color: DARKBLUE}}>
            Submit
          </Title>
        </Button>
      </View>
    </ScreenContainer>
  )
}
