import { useNavigation } from '@react-navigation/native'
import React, { useEffect, useState } from 'react'
import { useContext } from 'react'
import { ScreenContainer } from '../../components/ScreenContainer/ScreenContainer'
import { TableCell } from '../../components/TableCell/TableCell'
import { TableView } from '../../components/TableView/TableView'
import { AuthContext } from '../../providers/AuthProvider/AuthProvider'
import { getCategoryPosts, getRadius } from '../../api/service'
import * as Location from 'expo-location';

export const Recreation = () => {

  const [recreation, setRecreation] = useState();

  const navigation = useNavigation()
  const { credentials } = useContext(AuthContext)

  useEffect(() => {
    ( async () => {
      let location = await Location.getCurrentPositionAsync({});
      //@ts-ignore
      const { coords } = location
      const longitude = coords.longitude
      const latitude = coords.latitude
      getRadius({longitude, latitude}).then((resp) => {
        getCategoryPosts({token: credentials, longitude, radius: resp.radius, latitude,  category:'Recreation' }).then((recreationData) => setRecreation(recreationData))
      })
    })();
  }, []); 

  const route = (item: any) => navigation.navigate("Post", { data: item })
  const postTableCell = ({ item }: any) => <TableCell item={item} nav={ route } />

  return (
    <ScreenContainer>
      <TableView 
        data={recreation} 
        tableCellRenderer={postTableCell}
        scrollEnabled={false}
      />
    </ScreenContainer>
  )
}
