import { useNavigation } from '@react-navigation/native'
import React, { useState } from 'react'
import { Alert, View } from 'react-native'
import { Button, Paragraph, Subheading, TextInput, Title } from 'react-native-paper'
import { passwordReset } from '../../api/service'
import { ScreenContainer } from '../../components/ScreenContainer/ScreenContainer'
import { styles } from './styles'

export const ForgotPassword = () => {
  const [email, setEmail] = useState('')
  const navigation = useNavigation()

  const resetPasswordAction = () => {
    passwordReset(email)
    .then(() => navigation.navigate('Password Validation', {data: email}))
    .catch((e) => {
      Alert.alert(
        'Unable To Sign In.',
        'Your username or password are incorrect.',
        [
          { text: 'OK' },
        ],
      )
    })
  }
  return (
    <ScreenContainer>
      <View style={{ flex: 1, marginHorizontal: '8%', marginTop: '24%'}}>
        <Title>We'll send you a code that confirms the account is yours.</Title>
        <TextInput
          placeholder="email"
          onChangeText={(usernameText) => setEmail(usernameText)}
          value={email}
          style={styles.textInput}
          autoCapitalize='none'
          underlineColor='transparent'
          mode="outlined"
          left={<TextInput.Icon name='account'/>}
        />
        <Button
          mode="contained"
          style={styles.continueButton}
          onPress={() => resetPasswordAction()}
        >
          <Paragraph style={styles.buttonText}>Continue</Paragraph>
        </Button>
      </View>
    </ScreenContainer>
  )
}
